scripts.fetchUserProfile:
function(accessToken, ctx, cb) {
  request.get({
    url: 'https://public-api.wordpress.com/rest/v1/me/',
    headers: {
      'Authorization': 'bearer ' + accessToken
    }
  }, function(err, resp, body) {
    if (err) return cb(err);
    if (resp.statusCode !== 200) return cb(new Error(body));

    var providerResponse = JSON.parse(body);
    if (!providerResponse) return cb(new Error(body));

    var profile = {
      user_id: providerResponse.ID,
      name: providerResponse.display_name,
      email: providerResponse.email,
      picture: providerResponse.avatar_URL
    };

    cb(null, profile);
  });
}
